import React, { useState } from 'react'
import Modal from 'react-modal';
import Calendar from 'react-calendar';
import DatePicker from "react-datepicker";
import "react-datepicker/dist/react-datepicker.css";
import { ToastContainer, Toast, toast } from "react-toastify"

const customStyles = {
    content: {
        top: '50%',
        left: '50%',
        right: 'auto',
        bottom: 'auto',
        marginRight: '-50%',
        transform: 'translate(-50%, -50%)',
    },
};
Modal.setAppElement('#root');


const Invitation = (props) => {
    const [locations, setLocations] = useState();
    const [startDate, setStartDate] = useState(new Date());

    const closeModal = () => {
        props.setIsOpen(false);
    }

    const handleLocation = (e) => {
        if (e.target.value !== 'Residence') {
            toast.error("Erorr", { theme: 'dark' });
        } else {
            console.log(e.target.value)
            // setLocations(e.target.value)
        }
    }



    return (
        <div>
            <Modal
                isOpen={props.isOpen}
                onRequestClose={closeModal}
                style={customStyles}
                contentLabel="Example Modal"
            >
                <div>
                    <h1>Invite Your Friend</h1>
                    <form>
                        <div className='form'>
                            <label htmlFor='name'>Name</label>
                            <input type='text' name='name' placeholder='Name' />
                        </div>
                        <div className='form'>
                            <label htmlFor='surname'>Surname</label>
                            <input type='text' name='surname' placeholder='Surname' />
                        </div>
                        <div className='form'>
                            <label htmlFor='id'>ID Number</label>
                            <input type='text' name='id' placeholder='Id Number' />
                        </div>
                        <div className='form'>
                            <label htmlFor='location'>Location of visiting place</label>
                            <select id='location' value={locations} onChange={(e) => handleLocation(e)}>
                                <optgroup label='Access to whole  campus'>
                                    <option value="General">General (Blue badge)</option>
                                </optgroup>
                                <optgroup label='Access only the academic building'>
                                    <option value="Academic">Academic (Yellow badge)</option>
                                </optgroup>
                                <optgroup label='Access only the residential areas'>
                                    <option value="Residence" selected>Residence (Green badge)</option>
                                </optgroup>
                            </select>
                        </div>
                        <div className='form'>
                            <label htmlFor='duration'>Duration: </label>
                            <select id='duration'>
                                <option value='one'>One full day.</option>
                                <option value='multiple'>Multiple days.</option>
                            </select>
                            <DatePicker
                                selected={startDate}
                                onChange={(date: Date) => setStartDate(date)}
                            />
                        </div>
                        <div className='form'>
                            <button type='submit'>Invite</button>
                        </div>
                    </form>
                </div>
                <div className='form'>
                    <button onClick={closeModal} className='btn'>close</button>
                </div>
            </Modal>
            <ToastContainer />
        </div>
    )
}

export default Invitation
