import React, { useState, useEffect, useRef } from 'react'
import axios from 'axios'


const Administration = () => {
    const [data, setData] = useState([])
    const searchValue = useRef('');




    const handleChange = () => {
        // setSearchTerm(searchValue.current.value);
    }

    useEffect(() => {
        searchValue.current.focus();
        const getUsers = async () => {
            await axios.get('http://localhost:4000/admin')
                .then((users) => setData(users.data))
                .catch((err) => console.log(err))
        }
        getUsers();
    }, [])

    const addToBlackList = async (id) => {
        await axios.post(`http://localhost:4000/admin/${id}`)
            .then((users) => setData(users.data))
            .catch((err) => console.log(err))
    }


    const edit = async (id, feature) => {
        let value;
        if (feature === 'blacked') {
            value = prompt("Are you sure?")
        } else {
            value = prompt(`Enter your new ${feature}`)
        }

        await axios.post(`http://localhost:4000/admin/edit/${id}/${feature}/${value}`)
            .then((users) => setData(users.data))
            .catch((err) => console.log(err))

    }

    const handleSubmit = (e) => {
        e.preventDefault();
    }

    return (
        <div>
            <h1 style={{ color: 'white' }}>Monitoring people Entering the university</h1>
            <section className='section-search'>
                <div className='form-control'>
                    <form className='search-form' onSubmit={handleSubmit}>
                        <label htmlFor='visitor'>Search Visitor by id Or by Name</label>
                        <input type='text' id='visitor' name='vsitor' ref={searchValue} onChange={handleChange} />
                    </form>
                </div>
            </section>
            {data.map((user, index) => {
                const { blacked, id, name, password, phone, status, surname, } = user
                return (
                    <div key={index}>
                        <h1>User {index + 1}</h1>
                        <ul>
                            <li><span className='info admin'>Id:</span> <span className='item'>{id}</span></li>
                            <div>
                                <li ><span className='info admin'>Name</span>: <span className='item'>{name}</span></li>
                                <button className='edit' onClick={() => edit(id, 'name')}>Edit Name</button>
                            </div>
                            <div>
                                <li><span className='info admin'>Password:</span> <span className='item'>{password}</span></li>
                                <button className='edit' onClick={() => edit(id, 'password')}>Edit Password</button>
                            </div>
                            <div>
                                <li><span className='info admin'>Phone:</span> <span className='item'>{phone}</span></li>
                                <button className='edit' onClick={() => edit(id, 'phone')}>Edit Phone</button>
                            </div>
                            <div>
                                <li><span className='info admin'>Status:</span> <span className='item'>{status}</span></li>
                                <button className='edit' onClick={() => edit(id, 'status')}>Edit Status</button>
                            </div>
                            <div>
                                <li><span className='info admin'>Surname:</span> <span className='item'>{surname}</span></li>
                                <button className='edit' onClick={() => edit(id, 'surname')}>Edit Surname</button>
                            </div>
                            <div>
                                {blacked ? <li><span className='info admin'>In the Black-list:</span> <span className='item'>true</span></li>
                                    : <li><span className='info admin'>In the Black-list:</span> <span className='item'>false</span></li>}
                                <button className='edit' onClick={() => edit(id, 'blacked')}>Remove From Black-list</button>
                            </div>
                            <li><button className='addblack' onClick={() => addToBlackList(id)}>Throw to black-list</button></li>
                        </ul>
                    </div>
                )
            })}
        </div>
    )
}

export default Administration
