import React, { useState, useEffect } from 'react'
import axios from 'axios'


const Administration = () => {
    const [data, setData] = useState([])

    useEffect(() => {
        const getUsers = async () => {
            await axios.get('http://localhost:4000/admin')
                .then((users) => setData(users.data))
                .catch((err) => console.log(err))
        }
        getUsers();
    }, [])

    const addToBlackList = async (id) => {
        console.log("Clicked")
        await axios.post(`http://localhost:4000/admin/${id}`)
            .then((users) => setData(users.data))
            .catch((err) => console.log(err))
    }


    const edit = async (id, feature) => {
        let value;
        if (feature === 'blacked') {
            value = prompt("Are you sure?")
        } else {
            value = prompt(`Enter your new ${feature}`)
        }

    }

    return (
        <div>
            {data.map((user, index) => {
                const { blacked, id, name, password, phone, status, surname, } = user
                return (
                    <div key={index}>
                        <h1>User {index + 1}</h1>
                        <ul>
                            <li><span className='info admin'>Id:</span> {id}</li>
                            <div>
                                <li><span className='info admin'>Name</span>: {name}</li>
                                <button className='edit' onClick={() => edit(id, 'name')}>Edit Name</button>
                            </div>
                            <div>
                                <li><span className='info admin'>Password:</span> {password}</li>
                                <button className='edit' onClick={() => edit(id, 'psassword')}>Edit Password</button>
                            </div>
                            <div>
                                <li><span className='info admin'>Phone:</span> {phone}</li>
                                <button className='edit' onClick={() => edit(id, 'phone')}>Edit Phone</button>
                            </div>
                            <div>
                                <li><span className='info admin'>Status:</span> {status}</li>
                                <button className='edit' onClick={() => edit(id, 'status')}>Edit Status</button>
                            </div>
                            <div>
                                <li><span className='info admin'>Surname:</span> {surname}</li>
                                <button className='edit' onClick={() => edit(id, 'surname')}>Edit Surname</button>
                            </div>
                            <div>
                                {blacked ? <li><span className='info admin'>In the Black-list:</span> true</li>
                                    : <li><span className='info admin'>In the Black-list:</span> false</li>}
                                <button className='edit' onClick={() => edit(id, 'blacked')}>Remove From Black-list</button>
                            </div>
                            <li><button style={{ background: '#A43118' }} onClick={() => addToBlackList(id)}>Throw to black-list</button></li>
                        </ul>
                    </div>
                )
            })}
        </div>
    )
}

export default Administration
