import React, { useState, useEffect } from 'react'
import axios from 'axios'


const Administration = () => {
    const [data, setData] = useState([])

    useEffect(() => {
        const getUsers = async () => {
            await axios.get('http://localhost:4000/admin')
                .then((users) => setData(users.data))
                .catch((err) => console.log(err))
        }
        getUsers();
    }, [])
    return (
        <div>
            {data.map((user, index) => {
                const { id, name, password, phone, status, surname } = user

                return (
                    <div key={index}>
                        <h1>User {index + 1}</h1>
                        <ul>
                            <li><span className='info admin'>Id:</span> {id}</li>
                            <li><span className='info admin'>Name</span>: {name}</li>
                            <li><span className='info admin'>Password:</span> {password}</li>
                            <li><span className='info admin'>Phone:</span> {phone}</li>
                            <li><span className='info admin'>Status:</span> {status}</li>
                            <li><span className='info admin'>Surname:</span> {surname}</li>
                            <li><button style={{ background: red }}>Throw to black-list</button></li>
                        </ul>
                    </div>
                )
            })}
        </div>
    )
}

export default Administration
