import React, { useState, useEffect } from 'react'
import axios from 'axios'
import Modal from 'react-modal';
import { useTable, useSortBy, useFilters, useGlobalFilter, useAsyncDebounce } from 'react-table';

const customStyles = {
    content: {
        top: '50%',
        left: '50%',
        right: 'auto',
        bottom: 'auto',
        marginRight: '-50%',
        transform: 'translate(-50%, -50%)',
    },
};
Modal.setAppElement('#root');


const InvitedUser = (props) => {
    const [visitors, setVisitors] = useState([])

    const closeModal = () => {
        props.setIsOpen(false);
    }


    console.log(visitors)

    useEffect(() => {
        const getVisitors = async () => {
            await axios.get(`http://localhost:4000/visitors/${props.user}`)
                .then((vis) => setVisitors(vis.data[0].visitors))
                .catch((err) => console.log(err))
        }
        getVisitors();
    }, [])

    const removeVisitor = () => {
        const answer = prompt("Are you sure you want to remove this visitor?")
        if (answer === 'yes') {
            console.log("Removed")
        } else {
            console.log("Think carefully next time...")
        }
    }

    return (
        <Modal
            isOpen={props.isOpen}
            onRequestClose={closeModal}
            style={customStyles}
            contentLabel="Example Modal"
        >
            {visitors.map((vis, index) => {
                const { car, date, duration, id, location, name, surname } = vis;
                return (
                    <div className='main-container' key={index}>
                        <div className='visitor-container'>
                            <div className='each-info'>
                                <span className='info admin'>Name:</span>
                                <span className='vis-info'>{name}</span>
                            </div>
                            <div className='each-info'>
                                <span className='info admin'>Surname:</span>
                                <span className='vis-info'>{surname}</span>
                            </div>
                            <div className='each-info'>
                                <span className='info admin'>Location:</span>
                                <span className='vis-info'>{location}</span>
                            </div>
                            <div className='each-info'>
                                <span className='info admin'>Id:</span>
                                <span className='vis-info'>{id}</span>
                            </div>
                            <div className='each-info'>
                                <span className='info admin'>Duration:</span>
                                <span className='vis-info'>{duration}</span>
                            </div>
                            <div className='each-info'>
                                <span className='info admin'>Date:</span>
                                <span className='vis-info'>{date}</span>
                            </div>
                            <div className='each-info'>
                                <span className='info admin'>Car:</span>
                                <span className='vis-info'>{car}</span>
                            </div>
                        </div>
                        <div className='form'>
                            <button className='btn' onClick={() => removeVisitor()}>Remove Visitor</button>
                        </div>
                    </div>
                )
            })}
            <div className='form'>
                <button onClick={closeModal} className='btn' >close</button>
            </div>
        </Modal>
    )
}

export default InvitedUser
