import React, { useState, useContext, useEffect } from 'react'
import { useCallback } from 'react'
import axios from 'axios'

const AppContext = React.createContext()

const AppProvider = ({ children }) => {
    const [loading, setLoading] = useState(true)
    const [searchTerm, setSearchTerm] = useState('')
    const [people, setPoeple] = useState([])


    const getPeople = useCallback(async () => {
        setLoading(true);
        try {
            const vis = await axios.post('http://localhost:4000/getPeople')
            const data = vis.data.map((user) => {
                const result = user.visitors.map((data) => {
                    if (data.name.toUpperCase().includes(searchTerm.toUpperCase()) || data.id.includes(searchTerm)) {
                        return data;
                    }
                })
                if (result) {
                    let finalResult = {
                        user_name: user.name,
                        user_surname: user.surname,
                        user_status: user.status,
                        visitors: result
                    }
                    return finalResult;
                }

            })
            console.log(data);
            setLoading(false);
        } catch (err) {
            console.log(err.message)
            setLoading(false);
        }

    }, [searchTerm])

    useEffect(() => {
        getPeople();
    }, [setSearchTerm, getPeople])

    return (
        <AppContext.Provider value={{
            loading, searchTerm, setSearchTerm, people,
        }}>
            {children}
        </AppContext.Provider>
    )
}

export const useGlobalContext = () => {
    return useContext(AppContext)
}

export { AppContext, AppProvider }
